import tinycolor from 'tinycolor2';
import { get } from '../../../theme/src/theme-tools';
var baseStyle = {
  height: 16,
  paddingY: 0,
  paddingX: 6,
  borderRadius: 'radii.1',
  fontSize: '11.5px',
  textAlign: 'center',
  textDecoration: 'none',
  textTransform: 'uppercase'
};

function getContrastingColor(theme, props) {
  var scheme = get(theme, "fills.".concat(props.appearance, ".").concat(props.color), {
    backgroundColor: props.color,
    color: tinycolor(props.color).isLight() ? 'colors.dark' : 'white'
  });
  return {
    color: scheme.color,
    backgroundColor: scheme.backgroundColor
  };
}

var appearances = {
  solid: getContrastingColor,
  subtle: getContrastingColor
};
var sizes = {};
export default {
  baseStyle: baseStyle,
  appearances: appearances,
  sizes: sizes
};
